termux-setup-storage -y && pkg update -y --assume-yes -o Dpkg::Options::="--force-confold" && pkg upgrade -y && pkg install proot-distro -y && proot-distro install ubuntu && echo "proot-distro login ubuntu" > $HOME/../usr/bin/fsu && chmod +x $HOME/../usr/bin/fsu && clear && cd && rm Ubuntu-in-Termux.sh && clear && PINK="\e[95m" && GREEN="\e[32m" && ENDCOLOR="\e[0m" && echo -e "${GREEN}===========================================" && echo -e "Great ! Ubuntu Installed Successfully !" && echo -e "===========================================${ENDCOLOR}" && echo -e "${PINK}︻デ═一 Always use 'fsu' to Run Ubuntu ${ENDCOLOR}" && echo -e "${PINK}︻デ═一 Always use 'exit' to Logout Ubuntu ${ENDCOLOR}" && fsu
